import React, { useState } from 'react';
import {
  Container,
  Typography,
  Box,
  Grid,
  Paper,
  Button,
  Fab,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  MenuItem,
  Alert,
} from '@mui/material';
import {
  Add as AddIcon,
  FilterList as FilterIcon,
} from '@mui/icons-material';
import TicketCard from '../components/TicketCard';

const Ticker = () => {
  const [tickets, setTickets] = useState([
    {
      id: 1,
      title: 'Fix Login Bug',
      details: 'Users are experiencing issues when trying to log in with their email addresses. The system shows an error message even with valid credentials.',
      endTime: '2024-01-15 14:30',
      status: 'in-progress',
      priority: 'high',
      backgroundColor: '#fff3e0',
    },
    {
      id: 2,
      title: 'Update User Profile',
      details: 'Implement the ability for users to update their profile information including name, email, and profile picture.',
      endTime: '2024-01-20 16:00',
      status: 'open',
      priority: 'medium',
      backgroundColor: '#e8f5e8',
    },
    {
      id: 3,
      title: 'Database Optimization',
      details: 'Optimize database queries to improve application performance. Current load times are above acceptable thresholds.',
      endTime: '2024-01-25 12:00',
      status: 'urgent',
      priority: 'high',
      backgroundColor: '#ffebee',
    },
    {
      id: 4,
      title: 'Add Dark Mode',
      details: 'Implement dark mode theme toggle for better user experience, especially in low-light environments.',
      endTime: '2024-02-01 10:00',
      status: 'open',
      priority: 'low',
      backgroundColor: '#f3e5f5',
    },
  ]);

  const [openCreateDialog, setOpenCreateDialog] = useState(false);
  const [newTicket, setNewTicket] = useState({
    title: '',
    details: '',
    endTime: '',
    status: 'open',
    priority: 'medium',
    backgroundColor: '#ffffff',
  });

  const backgroundColors = [
    { name: 'White', value: '#ffffff' },
    { name: 'Light Blue', value: '#e3f2fd' },
    { name: 'Light Green', value: '#e8f5e8' },
    { name: 'Light Orange', value: '#fff3e0' },
    { name: 'Light Red', value: '#ffebee' },
    { name: 'Light Purple', value: '#f3e5f5' },
    { name: 'Light Yellow', value: '#fffde7' },
  ];

  const handleCreateTicket = () => {
    if (newTicket.title && newTicket.details) {
      const ticket = {
        id: tickets.length + 1,
        ...newTicket,
      };
      setTickets([...tickets, ticket]);
      setNewTicket({
        title: '',
        details: '',
        endTime: '',
        status: 'open',
        priority: 'medium',
        backgroundColor: '#ffffff',
      });
      setOpenCreateDialog(false);
    }
  };

  const handleEditTicket = (ticketId) => {
    // For now, just show an alert. In a real app, this would open an edit dialog
    alert(`Edit ticket with ID: ${ticketId}`);
  };

  const getTicketsByStatus = (status) => {
    return tickets.filter(ticket => ticket.status === status);
  };

  return (
    <Container maxWidth="xl">
      <Box sx={{ mb: 4 }}>
        <Typography variant="h3" component="h1" gutterBottom>
          Ticket Management
        </Typography>
        <Typography variant="h6" color="text.secondary" gutterBottom>
          Manage and track your support tickets
        </Typography>
      </Box>

      <Grid container spacing={3}>
        {/* Left Panel - Create and Filters */}
        <Grid item xs={12} md={3}>
          <Paper sx={{ p: 3, height: 'fit-content' }}>
            <Typography variant="h6" gutterBottom>
              Actions
            </Typography>

            <Button
              variant="contained"
              fullWidth
              startIcon={<AddIcon />}
              onClick={() => setOpenCreateDialog(true)}
              sx={{ mb: 2 }}
            >
              Create New Ticket
            </Button>

            <Button
              variant="outlined"
              fullWidth
              startIcon={<FilterIcon />}
              sx={{ mb: 3 }}
            >
              Filter Tickets
            </Button>

            {/* Quick Stats */}
            <Typography variant="subtitle2" color="text.secondary" gutterBottom>
              Quick Stats
            </Typography>
            <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
              <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                <Typography variant="body2">Total Tickets:</Typography>
                <Typography variant="body2" fontWeight="bold">{tickets.length}</Typography>
              </Box>
              <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                <Typography variant="body2">Open:</Typography>
                <Typography variant="body2" fontWeight="bold">{getTicketsByStatus('open').length}</Typography>
              </Box>
              <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                <Typography variant="body2">In Progress:</Typography>
                <Typography variant="body2" fontWeight="bold">{getTicketsByStatus('in-progress').length}</Typography>
              </Box>
              <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                <Typography variant="body2">Urgent:</Typography>
                <Typography variant="body2" fontWeight="bold">{getTicketsByStatus('urgent').length}</Typography>
              </Box>
            </Box>
          </Paper>
        </Grid>

        {/* Right Panel - Ticket Cards */}
        <Grid item xs={12} md={9}>
          <Typography variant="h5" gutterBottom sx={{ mb: 3 }}>
            All Tickets
          </Typography>

          <Grid container spacing={3}>
            {tickets.map((ticket) => (
              <Grid item xs={12} sm={6} lg={4} key={ticket.id}>
                <TicketCard
                  title={ticket.title}
                  details={ticket.details}
                  endTime={ticket.endTime}
                  status={ticket.status}
                  priority={ticket.priority}
                  backgroundColor={ticket.backgroundColor}
                  onEdit={() => handleEditTicket(ticket.id)}
                />
              </Grid>
            ))}
          </Grid>

          {tickets.length === 0 && (
            <Box
              sx={{
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'center',
                py: 8,
                textAlign: 'center',
              }}
            >
              <Typography variant="h6" color="text.secondary" gutterBottom>
                No tickets found
              </Typography>
              <Typography variant="body2" color="text.secondary" sx={{ mb: 3 }}>
                Create your first ticket to get started
              </Typography>
              <Button
                variant="contained"
                startIcon={<AddIcon />}
                onClick={() => setOpenCreateDialog(true)}
              >
                Create Ticket
              </Button>
            </Box>
          )}
        </Grid>
      </Grid>

      {/* Floating Action Button for Mobile */}
      <Fab
        color="primary"
        aria-label="add"
        onClick={() => setOpenCreateDialog(true)}
        sx={{
          position: 'fixed',
          bottom: 16,
          right: 16,
          display: { xs: 'flex', md: 'none' },
        }}
      >
        <AddIcon />
      </Fab>

      {/* Create Ticket Dialog */}
      <Dialog open={openCreateDialog} onClose={() => setOpenCreateDialog(false)} maxWidth="sm" fullWidth>
        <DialogTitle>Create New Ticket</DialogTitle>
        <DialogContent>
          <Box sx={{ pt: 1, display: 'flex', flexDirection: 'column', gap: 2 }}>
            <TextField
              label="Title"
              fullWidth
              value={newTicket.title}
              onChange={(e) => setNewTicket({ ...newTicket, title: e.target.value })}
              required
            />
            <TextField
              label="Details"
              fullWidth
              multiline
              rows={4}
              value={newTicket.details}
              onChange={(e) => setNewTicket({ ...newTicket, details: e.target.value })}
              required
            />
            <TextField
              label="End Time"
              type="datetime-local"
              fullWidth
              value={newTicket.endTime}
              onChange={(e) => setNewTicket({ ...newTicket, endTime: e.target.value })}
              InputLabelProps={{ shrink: true }}
            />
            <TextField
              label="Status"
              select
              fullWidth
              value={newTicket.status}
              onChange={(e) => setNewTicket({ ...newTicket, status: e.target.value })}
            >
              <MenuItem value="open">Open</MenuItem>
              <MenuItem value="in-progress">In Progress</MenuItem>
              <MenuItem value="closed">Closed</MenuItem>
              <MenuItem value="urgent">Urgent</MenuItem>
            </TextField>
            <TextField
              label="Priority"
              select
              fullWidth
              value={newTicket.priority}
              onChange={(e) => setNewTicket({ ...newTicket, priority: e.target.value })}
            >
              <MenuItem value="low">Low</MenuItem>
              <MenuItem value="medium">Medium</MenuItem>
              <MenuItem value="high">High</MenuItem>
            </TextField>
            <TextField
              label="Background Color"
              select
              fullWidth
              value={newTicket.backgroundColor}
              onChange={(e) => setNewTicket({ ...newTicket, backgroundColor: e.target.value })}
            >
              {backgroundColors.map((color) => (
                <MenuItem key={color.value} value={color.value}>
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                    <Box
                      sx={{
                        width: 20,
                        height: 20,
                        backgroundColor: color.value,
                        border: '1px solid #ccc',
                        borderRadius: 1,
                      }}
                    />
                    {color.name}
                  </Box>
                </MenuItem>
              ))}
            </TextField>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenCreateDialog(false)}>Cancel</Button>
          <Button
            onClick={handleCreateTicket}
            variant="contained"
            disabled={!newTicket.title || !newTicket.details}
          >
            Create
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default Ticker;
